import axios from "axios";

const instance = axios.create({
    baseURL: 'http://localhost:8081/api',
    headers: {
        'Access-Control-Allow-Origin': '*',
        'Access-Control-Allow-Methods': PUT, DELETE
    }
})

export default instance;
